const updatePatternPosition=e=>{const t=document.querySelector(".slider---pattern");if(!e||!t)return;const r=parseFloat(e.style.right??0);t.style.width=100-r+"%"},observeSliderStyles=()=>{const e=document.querySelector(".slider---progress");new MutationObserver((t=>{t.forEach((t=>"attributes"===t.type&&updatePatternPosition(e)))})).observe(e,{attributes:!0,attributeFilter:["style"]})};document.addEventListener("DOMContentLoaded",observeSliderStyles);
